# coding:utf-8import tkinter as tkimport tkinter.messagebox as tmsgimport randomimport re# from matplotlib.tests.test_widgets import ax# ボタンがクリックされたときの処理def ButtonClick():    # テキスト入力欄に入力された文字列を取得    b = editbox1.get()    # メッセージとして表示する    tmsg.showinfo("入力されたテキスト", b)    isok = False    while isok == False:    #    myinput = input("数字を入力してください")        if not re.match(r"^\d\d\d\d$", b):            print("4桁の数字を入力して下さい")        else:            print("Ok",b)            isok = True            if isok:        hit = 0        blow = 0        for j in range(4):            if ax[j] == b[j]:                hit=hit+1                        print(hit)                for j in range(4):            for i in range(4):                if i==j:            # 同じ場所は、、スキップ                     continue                                if ax[i] == b[j] and ax[j] != b[j]:                    blow=blow + 1                    break                        print(blow)                if hit == 4:            tmsg.showinfo("当たり", "おめでとう")            root.destroy()        else:            tmsg.showinfo("ヒント", "ヒント")                       # ヒット数とブロー数を表示            rirekibox.insert(tk.END, b + "　／H:" + str(hit) + " B:" + str(blow) + "\n")ax=[""] *4for i in range(4):     ax[i]=str(random.randint(0,9))print(type(ax))aa=str(random.randint(0,9999))print(aa)print(type(aa))print(aa[0])print(type(aa[0])) print(aa[1])# メインのプログラム# ウィンドウを作るroot = tk.Tk()root.geometry("600x400")root.title("数当てゲーム")# 履歴表示のテキストボックスを作るrirekibox = tk.Text(root, font=("Helvetica", 14))rirekibox.place(x=400, y=0, width=200, height=400)# ラベルを作るlabel1 = tk.Label(root, text="数を入力してね", font=("Helvetica", 14))label1.place(x = 20, y = 20)# テキストボックスを作るeditbox1 = tk.Entry(width = 4, font=("Helvetica", 28))editbox1.place(x = 120, y = 60)# ボタンを作るbutton1 = tk.Button(root, text = "チェック", font=("Helvetica", 14), command=ButtonClick)button1.place(x = 220, y = 60)# ウィンドウを表示するroot.mainloop()
